mapper.select:
SELECT c.camp_id, c.sido_id, s.sido_name, c.category_id, cat.camp_category, c.camp_name, c.camp_homepage, c.camp_explanation, c.camp_phone, c.camp_character, a.camp_road_address, a.camp_number_address, a.longitude, a.latitude, r.avg_rating, f.f_elec, f.f_hot_water, f.f_wifi, f.f_firewood, f.f_trails, f.f_swimming, f.f_playground, f.f_market, f.f_toilets, f.f_showers, f.f_sinks, f.f_fire_extinguishers, nf.n_fishing, nf.n_hiking, nf.n_beach, nf.n_water_sports, nf.n_valley, nf.n_river, nf.n_pool, nf.n_youth, nf.n_farm, nf.n_kids, o.o_weekday, o.o_weekend, o.o_spring, o.o_summer, o.o_fall, o.o_winter, g.g_bed, g.g_tv, g.g_fridge, g.g_internet, g.g_toilet, g.g_aircon, g.g_heater, g.g_cook_tool FROM tbl_camp c JOIN tbl_sido s ON c.sido_id = s.sido_id JOIN tbl_camp_category cat ON c.category_id = cat.category_id JOIN tbl_address a ON c.camp_id = a.camp_id LEFT JOIN ( SELECT camp_id, AVG(camp_rate) AS avg_rating FROM tbl_review GROUP BY camp_id ) r ON c.camp_id = r.camp_id LEFT JOIN tbl_facilities f ON c.camp_id = f.camp_id LEFT JOIN tbl_near_facilities nf ON c.camp_id = nf.camp_id LEFT JOIN tbl_op o ON c.camp_id = o.camp_id LEFT JOIN tbl_glamping g ON c.camp_id = g.camp_id
AND c.sido_id = #{region}
AND c.category_id IN
#{item}
AND c.camp_name LIKE CONCAT('%', #{text}, '%')

SELECT c.camp_id, c.camp_name, COALESCE(r.avg_rating, 0) AS avg_rating, COALESCE(r.review_count, 0) AS review_count FROM tbl_camp c LEFT JOIN ( SELECT camp_id, AVG(camp_rate) AS avg_rating, COUNT(*) AS review_count FROM tbl_review GROUP BY camp_id ) r ON c.camp_id = r.camp_id ORDER BY CASE WHEN #{sortBy} = 'avgRating' THEN r.avg_rating WHEN #{sortBy} = 'reviewCount' THEN r.review_count ELSE c.camp_name END
ASC DESC
LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*)
FROM tbl_review
WHERE camp_id = #{campId}
